import { Request, Response } from 'express';
import { StatusCodes } from 'http-status-codes';
import UsersService from '../services/user';

export default class UsersController {
  async getById(req: Request, res: Response) {
    const { id } = req.params;
    const user = await UsersService.getById(Number(id))

    res.status(StatusCodes.OK).json(user)
  }

  async create(req: Request, res: Response) {
    const user = req.body;
    const token = await UsersService.create(user)

    res.status(StatusCodes.CREATED).json({token})
  }

  async login(req: Request, res: Response) {
    const user = req.body;
    const token = await UsersService.login(user)

    res.status(StatusCodes.OK).json({token})
  }
}

import { formatProducts, generateJWT } from '../utils';
import * as bcrypt from 'bcryptjs'
import Products from '../database/models/productsModel';
import Users from "../database/models/usersModel";
import IUser from '../interfaces/IUser';
import HttpException from '../shared/http.exception';
import { StatusCodes } from 'http-status-codes';

export default class UsersService {
  static async getById(id: number) {
    const user = await Users.findByPk(id,
      {include: {
        model: Products
      }}) as IUser

    const finalUser = {
      id: user.id,
      name: user.name,
      email: user.email,
      products: formatProducts(user.products)
    }

    return finalUser
  }

  static async create(user: Users) {
    const hash = bcrypt.hashSync(user.password, 10)

    const { id } = await Users.create({
      name: user.name,
      email: user.email,
      password: hash
    })

    const token = generateJWT(id, user.email, user.name)

    return token
  }

  static async login(user: Users) {
    const atualUser = await Users.findOne({where: { email: user.email}}) as Users

    if(!atualUser) throw new HttpException(StatusCodes.BAD_REQUEST, "User don't exists")

    const compare = bcrypt.compareSync(user.password, atualUser.password)

    if(compare === false) throw new HttpException(StatusCodes.BAD_REQUEST, "Wrong password")

    const token = generateJWT(atualUser.id, atualUser.email, atualUser.name)

    return token
  }
}

import IProduct from "./interfaces/IProduct"
import {sign, Secret, SignOptions } from 'jsonwebtoken';

export const formatProducts = (products: IProduct[]) => {
  const final = products.map((item) => ({
    id: item.id,
    name: item.name,
    image: item.image,
    price: item.price,
    months: item.Rents.months
  }))

  return final
}

export const generateJWT = (id: number, email: string, name: string) => {
  const jwtConfig: SignOptions = {
    expiresIn: '7d',
    algorithm: 'HS256',
  };

  const secret: Secret = process.env.JWT_SECRET || 'pselAllugator';

  const token = sign({ data: { id, email, name}}, secret, jwtConfig)

  return token
}

class HttpException extends Error {
  status: number;

  constructor(status: number, message: string) {
    super(message);
    this.status = status;
  }
}

export default HttpException;

import { NextFunction, Request, Response } from 'express';
import { StatusCodes } from 'http-status-codes';
import * as Joi from 'joi';
import HttpException from '../shared/http.exception';

export const newUserValidate = (req: Request, res: Response, next: NextFunction) => {
  const infos = req.body
  const newUserSchema = Joi.object({
    email: Joi.string().email().required(),
    password: Joi.string().min(5).required(),
    name: Joi.string().min(5).required(),
  })

  const test = newUserSchema.validate(infos);
  if(test.error) {
    throw new HttpException(StatusCodes.BAD_REQUEST, test.error.message)
  }

  next()
}

import { NextFunction, Request, Response } from 'express';
import { StatusCodes } from 'http-status-codes';
import * as Joi from 'joi';
import HttpException from '../shared/http.exception';

export const loginValidate = (req: Request, res: Response, next: NextFunction) => {
  const infos = req.body
  const newUserSchema = Joi.object({
    email: Joi.string().email().required(),
    password: Joi.string().min(5).required(),
  })

  const test = newUserSchema.validate(infos);
  if(test.error) {
    throw new HttpException(StatusCodes.BAD_REQUEST, test.error.message)
  }

  next()
}
